nav.navbar {
	position: fixed;
	z-index: 1;
	top: 0px;
	left: 0px;
	width: 100%;
	padding: 0.5rem 2rem;
	border-bottom: 1px solid var(--clr-neutral-black-100);
	background-color: var(--clr-neutral-white-200) !important;

	.logo {
	}
	.collapse {
		justify-content: flex-end;
	}

	ul {
		list-style-type: none;
		margin-bottom: 0;
		padding: 0px;
	}
	li {
		padding: 0.25rem 1.25rem;
	}
	a {
		text-decoration: none;
		font-size: var(--fs-100);
		font-weight: var(--wgt-500);
		color: var(--clr-neutral-black-400) !important;

		position: relative;
		padding: 0rem !important;

		&::after {
			position: absolute;
			opacity: 0;
			bottom: -10%;
			left: 0;
			content: "";
			width: 100%;
			height: 2px;
			background: var(--clr-primary-yellow-200);
			border-radius: 1px;
			transition: all 0.2s ease-in-out;
		}

		&:hover {
			&::after {
				bottom: 0;
				opacity: 1;
			}
		}
	}
	.navbar-toggler {
		border: none;
	}
}

